Class Mi
{

    public function Main($account='',$password='',$steps='')
    {
        if(!$account || !$password || !$steps)
        {
            return ['error'=>1,'message'=>'参数错误'];
        }
        $mobile = '/^(1(([3456789][0-9])|(47)))\d{8}$/';
        $email = "/^([_a-z0-9-]+)(\.[_a-z0-9-]+)*@([a-z0-9-]+)(\.[a-z0-9-]+)*(\.[a-z]{2,4})$/";
        if(!preg_match($mobile, $account) && !preg_match($email, $account))
        {
            return ['error'=>1,'message'=>'账号格式错误'];
        }

        if(count(explode('@',$account))==1){
            $account = '+86'.$account;
        }

        $REG_URL = 'https://api-user.huami.com/registrations/'.$account.'/tokens';
        $LOGIN_URL = 'https://account.huami.com/v2/client/login';

        $headers = [
            'Host'=>'api-user.huami.com',
			'app_name'=>'com.xiaomi.hm.health',
			'accept-language'=>'zh-CN',
			'content-type'=>'application/x-www-form-urlencoded; charset=UTF-8',
			'content-length'=>'209',
			'accept-encoding'=>'gzip',
			'user-agent'=>'okhttp/3.12.13',
        ];
        $data = [
            'state'=>'REDIRECTION',
			'client_id'=>'HuaMi',
			'password'=>$password,
			'redirect_uri'=>'https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html',
			'region'=>'us-west-2',
			'token'=>'refresh',
			'token'=>'access',
			'country_code'=>'CN'
        ];
        $redata = $this->request_by_curl($REG_URL,$data,$headers);
        if(!isset($redata['header']['redirect_url']) || $redata['header']['redirect_url']=='')
        {
            return ['error'=>1,'message'=>'redirect_url获取失败'];
        }
        $redirect_url = $redata['header']['redirect_url'];
        $redirect_url = explode('?',$redirect_url);
        parse_str($redirect_url['1'],$redirect_url_arr);
        if(!isset($redirect_url_arr['access']))
        {
            return ['error'=>1,'message'=>'执行失败,可能是账号密码错误','result'=>$redata];
        }
        $headers = [
            'Host'=>'account.huami.com',
			'app_name'=>'com.xiaomi.hm.health',
			'accept-language'=>'zh-CN',
			'content-type'=>'application/x-www-form-urlencoded; charset=UTF-8',
			'content-length'=>'602',
			'accept-encoding'=>'gzip',
			'user-agent'=>'okhttp/3.12.13',
        ];
		$data = [
            'dn'=>'account.huami.com,api-user.huami.com,app-analytics.huami.com,api-watch.huami.com,auth.huami.com,api-analytics.huami.com,api-mifit.huami.com',
			'app_version'=>'6.1.2',
			'source'=>'com.xiaomi.hm.health:6.1.2:50581',
			'country_code'=>'CN',
			'device_id'=>'981471bb6e1c8113',
			'third_name'=>'huami',
			'lang'=>'zh',
			'device_model'=>'android_phone',
			'allow_registration'=> false,
			'app_name'=>'com.xiaomi.hm.health',
			'code'=> $redirect_url_arr['access'],
			'grant_type'=> 'access_token'
        ];
        $redata = $this->request_by_curl2($LOGIN_URL,$data,$headers);
        $url = 'https://api-mifit-cn.huami.com/v1/data/band_data.json?&t='.intval(microtime(1)*1000);
        $head = array('Content-Type:application/x-www-form-urlencoded','apptoken:'.$redata->token_info->app_token);
        $data_json = $this->bbdata(date("Y-m-d"),$steps);
        $data = 'userid='.$redata->token_info->user_id.'&last_sync_data_time='.time().'&device_type=0&last_deviceid=DA932FFFFE8816E7&data_json='.$data_json;
        $d3 = $this->request_by_curl($url,$data,$head,);
        return json_decode($d3['data'],1);
    }

